#!/bin/bash\n\n# Client script for testing the LLM Trading Bot API\n\n# Load environment variables\nsource .env\n\n# Set the base URL (change this if running on a different host/port)\nBASE_URL=\"http://localhost:8000\"\n\necho \"LLM Trading Bot API Test Client\"\necho \"================================\"\n\n# Function to check API health\ncheck_health() {\n    echo \"Checking API health...\"\n    curl -s -w \"\\n%{http_code}\\n\" -X GET \"$BASE_URL/api/v1/health\"\n}\n\n# Function to get market data\nget_market_data() {\n    local asset=${1:-\"EURUSD\"}\n    echo \"Getting market data for $asset...\"\n    curl -s -X GET \"$BASE_URL/api/v1/market/$asset\" | jq\n}\n\n# Function to analyze market with LLM\nanalyze_market() {\n    local asset=${1:-\"EURUSD\"}\n    echo \"Analyzing market for $asset with LLM...\"\n    curl -s -X POST \"$BASE_URL/api/v1/analyze\" \\\n         -H \"Content-Type: application/json\" \\\n         -d \"{\\\"asset\\\": \\\"$asset\\\"}\" | jq\n}\n\n# Function to execute a trade\nexecute_trade() {\n    local asset=${1:-\"EURUSD\"}\n    local direction=${2:-\"call\"}\n    local amount=${3:-\"10\"}\n    local duration=${4:-\"60\"}\n    \n    echo \"Executing $direction trade for $asset, amount: $amount, duration: $duration...\"\n    curl -s -X POST \"$BASE_URL/api/v1/trade\" \\\n         -H \"Content-Type: application/json\" \\\n         -d \"{\\n               \\\"asset\\\": \\\"$asset\\\",\\n               \\\"direction\\\": \\\"$direction\\\",\\n               \\\"amount\\\": $amount,\\n               \\\"duration\\\": $duration\\n             }\" | jq\n}\n\n# Parse command line arguments\ncase $1 in\n    health)\n        check_health\n        ;;\n    market)\n        get_market_data $2\n        ;;\n    analyze)\n        analyze_market $2\n        ;;\n    trade)\n        execute_trade $2 $3 $4 $5\n        ;;\n    *)\n        echo \"Usage: $0 {health|market|analyze|trade} [parameters]\"\n        echo \"  health          - Check API health\"\n        echo \"  market [asset]  - Get market data for asset (default: EURUSD)\"\n        echo \"  analyze [asset] - Analyze market with LLM for asset (default: EURUSD)\"\n        echo \"  trade [asset] [direction] [amount] [duration] - Execute a trade (defaults: EURUSD call 10 60)\"\n        ;;\nesac\n